// frontend/src/pages/PedirTurno.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api, { getPuntosAtencion, createTurno } from '../services/api';
import './PedirTurno.css';

const PedirTurno = ({ user: userProp, setUser }) => {
  const [puntosAtencion, setPuntosAtencion] = useState([]);
  const [puntoAtencionId, setPuntoAtencionId] = useState('');
  const [selectedPunto, setSelectedPunto] = useState(null);
  const [profesionales, setProfesionales] = useState([]);
  const [profesionalId, setProfesionalId] = useState('');
  const [tipoCita, setTipoCita] = useState('medica');
  const [fechaCita, setFechaCita] = useState('');
  const [horaCita, setHoraCita] = useState('');
  const [horasDisponibles, setHorasDisponibles] = useState([]);
  const [prioridad, setPrioridad] = useState('N');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const user = userProp || JSON.parse(localStorage.getItem('user')) || {};

  const baseHoras = ['08:00', '09:00', '10:00', '11:00', '14:00', '15:00'];

  useEffect(() => {
    const fetchPuntos = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token || !user.id) {
          setError('Debes iniciar sesión para pedir un turno.');
          navigate('/login');
          return;
        }
        const data = await getPuntosAtencion();
        const puntosConProfesional = data.filter(p => p.profesional);
        setPuntosAtencion(puntosConProfesional);
        if (puntosConProfesional.length > 0) {
          setPuntoAtencionId(puntosConProfesional[0].id.toString());
        }
      } catch (err) {
        setError('Error al cargar puntos de atención: ' + (err.response?.data?.detail || err.message));
      }
    };
    fetchPuntos();
  }, [navigate, setUser, user.id]);

  useEffect(() => {
    const fetchPuntoData = async () => {
      if (puntoAtencionId) {
        try {
          const punto = puntosAtencion.find(p => p.id === parseInt(puntoAtencionId));
          setSelectedPunto(punto || null);
          setProfesionalId(''); // Resetear profesional al cambiar punto

          // Obtener profesionales del punto
          const profResponse = await api.get(`/puntos-atencion/${puntoAtencionId}/profesionales/`);
          setProfesionales(profResponse.data);
          if (profResponse.data.length > 0) {
            setProfesionalId(profResponse.data[0].id.toString()); // Seleccionar el primero por defecto
          }

          // Obtener disponibilidad si hay fecha seleccionada
          if (fechaCita) {
            const availResponse = await api.get(
              `/turnos/check-availability/?punto=${puntoAtencionId}&fecha=${fechaCita}`
            );
            const occupiedHoras = availResponse.data.occupied || [];
            console.log('Horas ocupadas:', occupiedHoras);
            const availableHoras = baseHoras.filter(hora => !occupiedHoras.includes(hora));
            setHorasDisponibles(availableHoras);
            setHoraCita(availableHoras[0] || '');
          } else {
            setHorasDisponibles(baseHoras);
            setHoraCita('');
          }
        } catch (err) {
          setError('Error al cargar datos del punto: ' + (err.response?.data?.detail || err.message));
        }
      }
    };
    fetchPuntoData();
  }, [puntoAtencionId, puntosAtencion, fechaCita]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      if (!puntoAtencionId) throw new Error('Selecciona un punto de atención.');
      if (!profesionalId) throw new Error('Selecciona un profesional.');
      if (!fechaCita || !horaCita) throw new Error('Selecciona fecha y hora para la cita.');

      const fechaCitaCompleta = `${fechaCita}T${horaCita}:00`;
      const turnoData = {
        usuario_id: user.id,
        punto_atencion_id: parseInt(puntoAtencionId),
        tipo_cita: tipoCita,
        prioridad,
        fecha_cita: fechaCitaCompleta,
        estado: "En espera",
      };
      console.log('Datos enviados:', turnoData);
      await createTurno(turnoData);
      setSuccess('Turno solicitado exitosamente.');
      setTimeout(() => navigate('/profile'), 1500);
    } catch (err) {
      setError(`Error al crear el turno: ${err.response?.data || err.message}`);
    }
  };

  return (
    <div className="pedir-turno-page">
      <div className="pedir-turno-container">
        <h2 className="pedir-turno-title">Pedir Turno</h2>
        {error && <p className="pedir-turno-error">{error}</p>}
        {success && <p className="pedir-turno-success">{success}</p>}
        <form onSubmit={handleSubmit} className="pedir-turno-form">
          <div className="pedir-turno-field">
            <label className="pedir-turno-label">Punto de Atención:</label>
            <select
              value={puntoAtencionId}
              onChange={(e) => setPuntoAtencionId(e.target.value)}
              className="pedir-turno-input"
              required
            >
              <option value="">Seleccione un punto</option>
              {puntosAtencion.map((punto) => (
                <option key={punto.id} value={punto.id}>
                  {punto.nombre}
                </option>
              ))}
            </select>
          </div>

          {selectedPunto && (
            <>
              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Profesional:</label>
                <select
                  value={profesionalId}
                  onChange={(e) => setProfesionalId(e.target.value)}
                  className="pedir-turno-input"
                  required
                  disabled={profesionales.length === 0}
                >
                  <option value="">Seleccione un profesional</option>
                  {profesionales.map((prof) => (
                    <option key={prof.id} value={prof.id}>
                      {prof.nombre} (Cédula: {prof.cedula})
                    </option>
                  ))}
                </select>
                {profesionales.length === 0 && (
                  <p className="pedir-turno-note">No hay profesionales disponibles para este punto.</p>
                )}
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Servicios Disponibles:</label>
                <p className="pedir-turno-servicios">
                  {selectedPunto.servicios_texto || 'No se especificaron servicios'}
                </p>
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Tipo de Cita:</label>
                <select
                  value={tipoCita}
                  onChange={(e) => setTipoCita(e.target.value)}
                  className="pedir-turno-input"
                >
                  <option value="medica">Cita Médica</option>
                  <option value="odontologica">Cita Odontológica</option>
                </select>
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Fecha de la Cita:</label>
                <input
                  type="date"
                  value={fechaCita}
                  onChange={(e) => setFechaCita(e.target.value)}
                  min={new Date().toISOString().split('T')[0]}
                  className="pedir-turno-input"
                  required
                />
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Hora de la Cita:</label>
                <select
                  value={horaCita}
                  onChange={(e) => setHoraCita(e.target.value)}
                  className="pedir-turno-input"
                  required
                  disabled={!fechaCita || horasDisponibles.length === 0}
                >
                  <option value="">Seleccione una hora</option>
                  {horasDisponibles.map(hora => (
                    <option key={hora} value={hora}>{hora}</option>
                  ))}
                </select>
                {!fechaCita && <p className="pedir-turno-note">Selecciona una fecha primero.</p>}
                {fechaCita && horasDisponibles.length === 0 && (
                  <p className="pedir-turno-note">No hay horarios disponibles para esta fecha.</p>
                )}
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Prioridad:</label>
                <select
                  value={prioridad}
                  onChange={(e) => setPrioridad(e.target.value)}
                  className="pedir-turno-input"
                >
                  <option value="N">Normal</option>
                  <option value="P">Alta</option>
                </select>
              </div>
            </>
          )}

          <button
            type="submit"
            className="pedir-turno-button"
            disabled={!selectedPunto || !profesionalId || !fechaCita || !horaCita}
          >
            Solicitar Turno
          </button>
        </form>
      </div>
    </div>
  );
};

export default PedirTurno;
