// frontend/src/pages/AppointmentHistory.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import './AppointmentHistory.css';

const AppointmentHistory = ({ user, setUser }) => {
  const [appointments, setAppointments] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token || !user) {
          setError('Debes iniciar sesión para ver tu historial.');
          navigate('/login');
          return;
        }
        const response = await api.get('turnos/');
        setAppointments(response.data);
        console.log('Turnos obtenidos:', response.data);
      } catch (err) {
        if (err.response?.status === 401) {
          setError('Sesión expirada. Por favor, inicia sesión nuevamente.');
          localStorage.removeItem('token');
          localStorage.removeItem('refreshToken');
          localStorage.removeItem('user');
          setUser(null);
          navigate('/login');
        } else {
          setError('Error al cargar el historial de citas');
        }
      }
    };
    fetchAppointments();
  }, [user, navigate, setUser]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('es-CO', { dateStyle: 'medium', timeStyle: 'short' });
  };

  return (
    <div className="appointment-history-container">
      <h2 className="appointment-history-title">Historial de Citas</h2>
      {error && <p className="appointment-history-error">{error}</p>}
      {appointments.length === 0 && !error ? (
        <p className="appointment-history-empty">No tienes citas registradas.</p>
      ) : (
        <ul className="appointment-history-list">
          {appointments.map((appointment) => (
            <li key={appointment.id} className="appointment-history-item">
              <div className="appointment-history-details">
                <span className="appointment-history-number">Turno: {appointment.numero}</span>
                <span>Punto: {appointment.punto_atencion}</span>
                <span>Tipo: {appointment.tipo_cita}</span>
                <span>Fecha: {formatDate(appointment.fecha_cita)}</span>
                <span>Estado: {appointment.estado}</span>
                <span>Prioridad: {appointment.prioridad === 'N' ? 'Normal' : 'Alta'}</span>
                <p>Descripción: {appointment.descripcion}</p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AppointmentHistory;
