// frontend/src/pages/PedirTurno.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getPuntosAtencion, createTurno } from '../services/api';
import './PedirTurno.css';

const PedirTurno = ({ user: userProp, setUser }) => {
  const [puntosAtencion, setPuntosAtencion] = useState([]);
  const [puntoAtencionId, setPuntoAtencionId] = useState('');
  const [selectedPunto, setSelectedPunto] = useState(null);
  const [tipoCita, setTipoCita] = useState('medica');
  const [fechaCita, setFechaCita] = useState('');
  const [horaCita, setHoraCita] = useState('08:00');
  const [prioridad, setPrioridad] = useState('N');
  const [descripcion, setDescripcion] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const user = userProp || {
    id: null,
    cedula: '',
    email: '',
    nombre: 'Usuario',
    es_profesional: false
  };

  console.log('Usuario en PedirTurno:', user);
  console.log('Renderizando PedirTurno con user:', user); // Nuevo log

  useEffect(() => {
    const fetchPuntos = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('Debes iniciar sesión para acceder a esta página.');
          navigate('/login');
          return;
        }
        const data = await getPuntosAtencion();
        console.log('Respuesta completa de getPuntosAtencion:', data);
        const puntosConProfesional = data.filter(p => p.profesional);
        setPuntosAtencion(puntosConProfesional);
        if (puntosConProfesional.length > 0) {
          setPuntoAtencionId(puntosConProfesional[0].id.toString());
        } else {
          setError('No hay puntos de atención disponibles con profesionales');
        }
      } catch (err) {
        if (err.response && err.response.status === 401) {
          setError('Sesión expirada o no autorizada. Por favor, inicia sesión nuevamente.');
          localStorage.removeItem('token');
          localStorage.removeItem('refreshToken');
          localStorage.removeItem('user');
          setUser(null);
          navigate('/login');
        } else {
          setError('Error al cargar puntos de atención');
        }
      }
    };
    fetchPuntos();
  }, [navigate, setUser]);

  useEffect(() => {
    const punto = puntosAtencion.find(p => p.id === parseInt(puntoAtencionId));
    setSelectedPunto(punto || null);
  }, [puntoAtencionId, puntosAtencion]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No hay token de autenticación');
      if (!user.id) throw new Error('No se encontró el ID del usuario');
      if (!puntoAtencionId || puntoAtencionId === '') throw new Error('Debe seleccionar un punto de atención');

      const fechaCitaCompleta = `${fechaCita}T${horaCita}:00-05:00`;
      const turnoData = {
        usuario_id: user.id,
        punto_atencion_id: parseInt(puntoAtencionId),
        tipo_cita: tipoCita,
        prioridad,
        descripcion,
        fecha_cita: fechaCitaCompleta,
      };
      await createTurno(turnoData);
      alert('Turno solicitado exitosamente');
      navigate('/');
    } catch (err) {
      setError(`Error al crear el turno: ${err.response ? JSON.stringify(err.response.data) : err.message}`);
    }
  };

  const horasDisponibles = ['08:00', '09:00', '10:00', '11:00', '14:00', '15:00'];

  return (
    <div className="pedir-turno-page">
      <div className="pedir-turno-container">
        <h2 className="pedir-turno-title">Pedir Turno</h2>
        {error && <p className="pedir-turno-error">{error}</p>}
        <form onSubmit={handleSubmit} className="pedir-turno-form">
          <div className="pedir-turno-field">
            <label className="pedir-turno-label">Punto de Atención:</label>
            <select
              value={puntoAtencionId}
              onChange={(e) => setPuntoAtencionId(e.target.value)}
              className="pedir-turno-input"
              required
            >
              <option value="">Seleccione un punto</option>
              {puntosAtencion.map((punto) => (
                <option key={punto.id} value={punto.id}>
                  {punto.nombre} - {punto.profesional ? punto.profesional : 'Sin profesional'}
                </option>
              ))}
            </select>
          </div>

          {selectedPunto && (
            <>
              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Servicios Disponibles:</label>
                <p className="pedir-turno-servicios">
                  {selectedPunto.servicios_texto || 'No se especificaron servicios'}
                </p>
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Tipo de Cita:</label>
                <select
                  value={tipoCita}
                  onChange={(e) => setTipoCita(e.target.value)}
                  className="pedir-turno-input"
                >
                  <option value="medica">Cita Médica</option>
                  <option value="odontologica">Cita Odontológica</option>
                </select>
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Fecha de la Cita:</label>
                <input
                  type="date"
                  value={fechaCita}
                  onChange={(e) => setFechaCita(e.target.value)}
                  min={new Date().toISOString().split('T')[0]}
                  className="pedir-turno-input"
                  required
                />
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Hora de la Cita:</label>
                <select
                  value={horaCita}
                  onChange={(e) => setHoraCita(e.target.value)}
                  className="pedir-turno-input"
                  required
                >
                  {horasDisponibles.map(hora => (
                    <option key={hora} value={hora}>{hora}</option>
                  ))}
                </select>
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Prioridad:</label>
                <select
                  value={prioridad}
                  onChange={(e) => setPrioridad(e.target.value)}
                  className="pedir-turno-input"
                >
                  <option value="N">Normal</option>
                  <option value="P">Alta</option>
                </select>
              </div>

              <div className="pedir-turno-field">
                <label className="pedir-turno-label">Descripción:</label>
                <textarea
                  value={descripcion}
                  onChange={(e) => setDescripcion(e.target.value)}
                  className="pedir-turno-textarea"
                  required
                />
              </div>
            </>
          )}

          <button
            type="submit"
            className="pedir-turno-button"
            disabled={!selectedPunto}
          >
            Solicitar Turno
          </button>
        </form>
      </div>
    </div>
  );
};

export default PedirTurno;
