import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { getTurnos, updateTurno, getProfesionalStats, logoutUser } from '../services/api';
import './ProfesionalDashboard.css';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

// Registrar componentes de Chart.js
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const ProfesionalDashboard = ({ user: userProp, setUser }) => {
  const [turnos, setTurnos] = useState([]);
  const [stats, setStats] = useState({ pacientes_atendidos: 0, citas_mas_pedidas: [], turnos_por_punto: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const user = userProp || JSON.parse(localStorage.getItem('user')) || {};

  const handleError = useCallback((err) => {
    if (err.response?.status === 401) {
      setError('Sesión expirada. Por favor, inicia sesión nuevamente.');
      localStorage.clear();
      setUser(null);
      navigate('/login');
    } else {
      setError(`Error: ${err.response?.data?.detail || err.message}`);
    }
    setLoading(false);
  }, [navigate, setUser]);

  const fetchData = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token || !user.es_profesional) {
        setError('Debes ser un profesional autenticado para acceder.');
        navigate('/login');
        return;
      }
      const [turnosData, statsData] = await Promise.all([
        getTurnos(),
        getProfesionalStats()
      ]);
      console.log('Turnos recibidos:', turnosData);
      setTurnos(turnosData);
      setStats(statsData);
      setLoading(false);
    } catch (err) {
      handleError(err);
    }
  }, [navigate, setUser, user.es_profesional, handleError]);

  const handleEstadoChange = async (turnoId, nuevoEstado) => {
    try {
      const turno = turnos.find(t => t.id === turnoId);
      if (!turno) throw new Error('Turno no encontrado');

      console.log('Datos del turno:', turno);
      console.log('Valor de punto_atencion_id_read:', turno.punto_atencion_id_read);

      const puntoAtencionId = parseInt(turno.punto_atencion_id_read);
      if (isNaN(puntoAtencionId)) {
        throw new Error('punto_atencion_id_read no es un número válido');
      }

      const data = {
        punto_atencion_id: puntoAtencionId,
        tipo_cita: turno.tipo_cita,
        estado: nuevoEstado,
        prioridad: turno.prioridad,
        descripcion: turno.descripcion || ''
      };

      console.log('Actualizando turno:', { id: turnoId, data });
      const updatedTurno = await updateTurno(turnoId, data);
      const updatedTurnos = turnos.map(t =>
        t.id === turnoId ? { ...t, ...updatedTurno } : t
      );
      setTurnos(updatedTurnos);
      fetchData();
      setError('');
    } catch (err) {
      console.error('Error completo:', err.response?.data || err.message);
      setError(`Error al actualizar el turno: ${err.response?.data?.detail || JSON.stringify(err.response?.data) || err.message}`);
    }
  };

  const handleLogout = async () => {
    try {
      await logoutUser();
      localStorage.clear();
      setUser(null);
      navigate('/login');
    } catch (err) {
      setError('Error al cerrar sesión');
    }
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 30000);
    return () => clearInterval(interval);
  }, [fetchData]);

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return `${date.toLocaleDateString('es-CO')} ${date.toLocaleTimeString('es-CO', { hour: '2-digit', minute: '2-digit' })}`;
  };

  // Datos para la gráfica de Pacientes Atendidos
  const chartData = {
    labels: ['Hoy'], // Podríamos usar más días si tienes datos históricos
    datasets: [
      {
        label: 'Pacientes Atendidos',
        data: [stats.pacientes_atendidos],
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Pacientes Atendidos Hoy',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 1,
        },
      },
    },
  };

  const turnoActual = turnos.find(turno => turno.estado === 'En progreso') ||
                     turnos.filter(turno => turno.estado === 'En espera')
                          .sort((a, b) => {
                            if (a.prioridad === b.prioridad) return a.numero.localeCompare(b.numero);
                            return a.prioridad === 'P' ? -1 : 1;
                          })[0];
  const turnosEnEspera = turnos.filter(turno => turno.estado === 'En espera')
                               .sort((a, b) => {
                                 if (a.prioridad === b.prioridad) return a.numero.localeCompare(b.numero);
                                 return a.prioridad === 'P' ? -1 : 1;
                               });

  return (
    <div className="profesional-dashboard">
      <div className="dashboard-content">
        <h2 className="dashboard-title">Dashboard Profesional</h2>
        {loading && <p className="dashboard-loading">Cargando...</p>}
        {error && <p className="dashboard-error">{error}</p>}
        {!loading && !error && (
          <div className="dashboard-grid">
            {/* Sidebar (Barra Izquierda) */}
            <div className="sidebar">
              <div className="sidebar-item" onClick={() => navigate('/profesional')}>
                <i className="fas fa-home"></i> Dashboard
              </div>
              <div className="sidebar-item" onClick={() => navigate('/profile')}>
                <i className="fas fa-user"></i> Perfil
              </div>
              <div className="sidebar-item" onClick={() => navigate('/update-profile')}>
                <i className="fas fa-cog"></i> Configuración
              </div>
              <div className="sidebar-item" onClick={handleLogout}>
                <i className="fas fa-sign-out-alt"></i> Cerrar Sesión
              </div>
            </div>

            {/* Fila 1 */}
            {/* Pacientes Atendidos */}
            <div className="dashboard-card pacientes-atendidos">
              <h3>Pacientes Atendidos Hoy</h3>
              <p className="big-number">{stats.pacientes_atendidos}</p>
              <div className="chart-container">
                <Bar data={chartData} options={chartOptions} />
              </div>
            </div>

            {/* Turno Actual */}
            <div className="dashboard-card turno-actual">
              <h3>Turno Actual</h3>
              {turnoActual ? (
                <div className="turno-details">
                  <p><strong>Número:</strong> {turnoActual.numero}</p>
                  <p><strong>Paciente:</strong> {turnoActual.usuario}</p>
                  <p><strong>Tipo:</strong> {turnoActual.tipo_cita}</p>
                  <p><strong>Fecha:</strong> {formatDateTime(turnoActual.fecha_cita)}</p>
                  <p><strong>Estado:</strong> {turnoActual.estado}</p>
                  <select
                    value={turnoActual.estado}
                    onChange={(e) => handleEstadoChange(turnoActual.id, e.target.value)}
                    className="estado-select"
                  >
                    <option value="En espera">En espera</option>
                    <option value="En progreso">En progreso</option>
                    <option value="Atendido">Atendido</option>
                    <option value="Cancelado">Cancelado</option>
                  </select>
                </div>
              ) : (
                <p>No hay turno actual.</p>
              )}
            </div>

            {/* Fila 2 */}
            {/* Pacientes a Atender */}
            <div className="dashboard-card pacientes-atender">
              <h3>Pacientes a Atender ({turnosEnEspera.length})</h3>
              {turnosEnEspera.length > 0 ? (
                <ul className="turno-list">
                  {turnosEnEspera.map(turno => (
                    <li key={turno.id} className="turno-item">
                      <span>{turno.numero} ({turno.prioridad === 'P' ? 'Prioritario' : 'Normal'}) - {turno.usuario} ({turno.tipo_cita})</span>
                      <button onClick={() => handleEstadoChange(turno.id, 'En progreso')} className="action-button">
                        Atender
                      </button>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No hay turnos en espera.</p>
              )}
            </div>

            {/* Turnos Activos */}
            <div className="dashboard-card turnos-por-punto">
              <h3>Turnos Activos</h3>
              <p>{stats.turnos_por_punto} turnos en {turnos.length > 0 ? turnos[0].punto_atencion : 'No asignado'}</p>
            </div>

            {/* Fila 3 */}
            {/* Calendario */}
            <div className="dashboard-card calendario">
              <h3>Calendario</h3>
              <Calendar />
            </div>

            {/* Citas Más Pedidas */}
            <div className="dashboard-card citas-mas-pedidas">
              <h3>Citas Más Pedidas</h3>
              {stats.citas_mas_pedidas.length > 0 ? (
                <ul>
                  {stats.citas_mas_pedidas.map((cita, index) => (
                    <li key={index}>{cita.tipo_cita}: {cita.total}</li>
                  ))}
                </ul>
              ) : (
                <p>No hay datos.</p>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfesionalDashboard;
