// frontend/src/components/Nav.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { logoutUser } from '../services/api';
import './Nav.css';

const Nav = ({ user, setUser }) => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    console.log('Nav renderizado con user:', user);
  }, [user]);

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('token');
      console.log('Token al intentar cerrar sesión:', token);
      console.log('Usuario actual:', user);
      if (!token) throw new Error('No hay token disponible');
      await logoutUser();
      localStorage.removeItem('token');
      localStorage.removeItem('refreshToken');
      localStorage.removeItem('user');
      setUser(null);
      console.log('Sesión cerrada exitosamente');
      setIsOpen(false);
      navigate('/login');
    } catch (err) {
      console.error('Error al cerrar sesión:', err.message || err);
      alert(`No se pudo cerrar sesión. Error: ${err.message || 'Desconocido'}`);
    }
  };

  const safeUserName = user && typeof user === 'object' ? user.nombre || 'Usuario' : 'Usuario';

  return (
    <nav className="nav">
      <div className="nav-container">
        <h1 className="nav-logo" onClick={() => navigate('/')}>
          Sistema de Turnos
        </h1>
        <button className="nav-menu-button" onClick={() => setIsOpen(!isOpen)}>
          ☰
        </button>
        <div className={`nav-menu ${isOpen ? 'open' : ''}`}>
          {user ? (
            <>
              <button
                className="nav-menu-item"
                onClick={() => {
                  navigate('/');
                  setIsOpen(false);
                }}
              >
                Inicio
              </button>
              {user.es_profesional ? (
                <button
                  className="nav-menu-item"
                  onClick={() => {
                    navigate('/profesional');
                    setIsOpen(false);
                  }}
                >
                  Dashboard
                </button>
              ) : (
                <button
                  className="nav-menu-item"
                  onClick={() => {
                    navigate('/pedir-turno');
                    setIsOpen(false);
                  }}
                >
                  Pedir Turno
                </button>
              )}
              <span className="nav-menu-item nav-user">
                Hola, {safeUserName}
              </span>
              <button className="nav-menu-item" onClick={handleLogout}>
                Cerrar Sesión
              </button>
            </>
          ) : (
            <button
              className="nav-menu-item"
              onClick={() => {
                navigate('/login');
                setIsOpen(false);
              }}
            >
              Iniciar Sesión
            </button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Nav;
