// frontend/src/pages/ProfesionalDashboard.js
import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { getTurnos, updateTurno } from '../services/api';
import './ProfesionalDashboard.css';

const ProfesionalDashboard = ({ user: userProp, setUser }) => {
  const [turnos, setTurnos] = useState([]);
  const [filteredTurnos, setFilteredTurnos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [estadoFilter, setEstadoFilter] = useState('');
  const [tipoFilter, setTipoFilter] = useState('');
  const [sortBy, setSortBy] = useState('fecha_cita');
  const [sortOrder, setSortOrder] = useState('asc');
  const navigate = useNavigate();
  const user = userProp || JSON.parse(localStorage.getItem('user')) || {};

  const handleError = useCallback((err) => {
    if (err.response?.status === 401) {
      setError('Sesión expirada. Por favor, inicia sesión nuevamente.');
      localStorage.removeItem('token');
      localStorage.removeItem('refreshToken');
      localStorage.removeItem('user');
      setUser(null);
      navigate('/login');
    } else {
      setError('Error al cargar turnos');
    }
    setLoading(false);
  }, [navigate, setUser]); // Dependencias de handleError

  const fetchTurnos = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token || !user.es_profesional) {
        setError('Debes ser un profesional autenticado para acceder.');
        navigate('/login');
        return;
      }
      const data = await getTurnos();
      setTurnos(data);
      setFilteredTurnos(data);
      setLoading(false);
    } catch (err) {
      handleError(err);
    }
  }, [navigate, setUser, user.es_profesional, handleError]); // Incluimos handleError

  const handleEstadoChange = async (turnoId, nuevoEstado) => {
    try {
      const response = await api.put(`/turnos/${turnoId}/`, { estado: newEstado });
      console.log('Turno actualizado:', response.data);
      // Actualizar estado local
    } catch (error) {
      console.error('Error al actualizar el turno:', error.message);
      setError('Error al actualizar el turno: ' + error.message);
    }
  };

  const filterAndSortTurnos = useCallback((turnosToFilter = turnos) => {
    let result = [...turnosToFilter];

    // Filtro por búsqueda
    if (searchTerm) {
      result = result.filter(turno =>
        turno.numero.toLowerCase().includes(searchTerm.toLowerCase()) ||
        turno.usuario.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filtro por estado
    if (estadoFilter) {
      result = result.filter(turno => turno.estado === estadoFilter);
    }

    // Filtro por tipo
    if (tipoFilter) {
      result = result.filter(turno => turno.tipo_cita === tipoFilter);
    }

    // Ordenamiento
    result.sort((a, b) => {
      const valueA = sortBy === 'fecha_cita' ? new Date(a[sortBy]) : a[sortBy];
      const valueB = sortBy === 'fecha_cita' ? new Date(b[sortBy]) : b[sortBy];
      return sortOrder === 'asc' ? (valueA > valueB ? 1 : -1) : (valueA < valueB ? 1 : -1);
    });

    setFilteredTurnos(result);
  }, [searchTerm, estadoFilter, tipoFilter, sortBy, sortOrder, turnos]);

  useEffect(() => {
    fetchTurnos();
  }, [fetchTurnos]);

  useEffect(() => {
    filterAndSortTurnos();
  }, [filterAndSortTurnos]);

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return {
      date: date.toLocaleDateString('es-CO'),
      time: date.toLocaleTimeString('es-CO', { hour: '2-digit', minute: '2-digit' }),
    };
  };

  return (
    <div className="dashboard-container">
      <h2 className="dashboard-title">Dashboard Profesional</h2>
      {loading && <p className="dashboard-loading">Cargando...</p>}
      {error && <p className="dashboard-error">{error}</p>}
      {!loading && !error && (
        <>
          <div className="dashboard-controls">
            <input
              type="text"
              placeholder="Buscar por número o cédula"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="dashboard-search"
            />
            <select
              value={estadoFilter}
              onChange={(e) => setEstadoFilter(e.target.value)}
              className="dashboard-filter"
            >
              <option value="">Todos los estados</option>
              <option value="En espera">En espera</option>
              <option value="En progreso">En progreso</option>
              <option value="Atendido">Atendido</option>
              <option value="Cancelado">Cancelado</option>
            </select>
            <select
              value={tipoFilter}
              onChange={(e) => setTipoFilter(e.target.value)}
              className="dashboard-filter"
            >
              <option value="">Todos los tipos</option>
              <option value="medica">Médica</option>
              <option value="consulta">Consulta</option>
            </select>
            <select
              value={`${sortBy}-${sortOrder}`}
              onChange={(e) => {
                const [by, order] = e.target.value.split('-');
                setSortBy(by);
                setSortOrder(order);
              }}
              className="dashboard-filter"
            >
              <option value="fecha_cita-asc">Fecha (Asc)</option>
              <option value="fecha_cita-desc">Fecha (Desc)</option>
              <option value="numero-asc">Número (Asc)</option>
              <option value="numero-desc">Número (Desc)</option>
            </select>
            <button onClick={fetchTurnos} className="dashboard-refresh">Refrescar</button>
          </div>

          <div className="turnos-table-wrapper">
            <h3 className="dashboard-subtitle">Mis Turnos</h3>
            {filteredTurnos.length > 0 ? (
              <table className="turnos-table">
                <thead>
                  <tr>
                    <th>Número</th>
                    <th>Paciente</th>
                    <th>Tipo</th>
                    <th>Fecha</th>
                    <th>Hora</th>
                    <th>Prioridad</th>
                    <th>Estado</th>
                    <th>Acción</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredTurnos.map((turno) => {
                    const { date, time } = formatDateTime(turno.fecha_cita);
                    return (
                      <tr key={turno.id} className={`turno-row estado-${turno.estado.toLowerCase().replace(' ', '-')}`}>
                        <td data-label="Número">{turno.numero}</td>
                        <td data-label="Paciente">{turno.usuario}</td>
                        <td data-label="Tipo">{turno.tipo_cita}</td>
                        <td data-label="Fecha">{date}</td>
                        <td data-label="Hora">{time}</td>
                        <td data-label="Prioridad">{turno.prioridad === 'N' ? 'Normal' : 'Alta'}</td>
                        <td data-label="Estado">{turno.estado}</td>
                        <td data-label="Acción">
                          <select
                            value={turno.estado}
                            onChange={(e) => handleEstadoChange(turno.id, e.target.value)}
                            className="estado-select"
                          >
                            <option value="En espera">En espera</option>
                            <option value="En progreso">En progreso</option>
                            <option value="Atendido">Atendido</option>
                            <option value="Cancelado">Cancelado</option>
                          </select>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            ) : (
              <p className="dashboard-text">No hay turnos que coincidan con los filtros.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default ProfesionalDashboard;
