// frontend/src/pages/PedirTurno.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getCurrentTurnos, createTurno } from '../services/api';
import './PedirTurno.css';

const PedirTurno = ({ user: userProp, setUser }) => {
  const [currentTurnos, setCurrentTurnos] = useState({ melendez: null, polvorines: null });
  const [estimatedTime, setEstimatedTime] = useState({ melendez: 0, polvorines: 0 });
  const [tipoCita, setTipoCita] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const user = userProp || JSON.parse(localStorage.getItem('user')) || {};

  useEffect(() => {
    const fetchCurrentTurnos = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token || !user.id) {
          setError('Debes iniciar sesión para pedir un turno.');
          navigate('/login');
          return;
        }
        const data = await getCurrentTurnos();
        setCurrentTurnos({
          melendez: data.melendez || 'Ninguno',
          polvorines: data.polvorines || 'Ninguno',
        });
        setEstimatedTime({
          melendez: data.melendezTime || 0,
          polvorines: data.polvorinesTime || 0,
        });
      } catch (err) {
        setError('Error al cargar los turnos actuales: ' + (err.response?.data?.detail || err.message));
        if (err.response?.status === 401) {
          localStorage.clear();
          setUser(null);
          navigate('/login');
        }
      }
    };
    fetchCurrentTurnos();
    const interval = setInterval(fetchCurrentTurnos, 30000); // Actualiza cada 30 segundos
    return () => clearInterval(interval);
  }, [navigate, setUser, user.id]);

  const handleRequestTurno = async (puntoAtencion) => {
    if (!tipoCita) {
      setError('Por favor, selecciona un tipo de servicio.');
      return;
    }
    setError('');
    setSuccess('');
    try {
      const turnoData = {
        punto_atencion: puntoAtencion === 'Melendez UCI' ? 1 : 2, // IDs fijos por ahora, ajustar según backend
        tipo_cita: tipoCita,
        usuario: user.id,
      };
      const newTurno = await createTurno(turnoData);
      setSuccess(`Turno ${newTurno.numero} solicitado con éxito en ${puntoAtencion}.`);
      setTipoCita('');
      setTimeout(() => navigate('/appointment-history'), 2000); // Redirige al historial
    } catch (err) {
      setError('Error al solicitar turno: ' + (err.response?.data?.detail || err.message));
    }
  };

  return (
    <div className="pedir-turno-page">
      <div className="pedir-turno-container">
        <h2 className="pedir-turno-title">Solicitar Turno - Día Actual</h2>
        {error && <p className="pedir-turno-error">{error}</p>}
        {success && <p className="pedir-turno-success">{success}</p>}

        {/* Turnos Actuales */}
        <div className="current-turnos">
          <div className="turno-info">
            <h3>Meléndez UCI</h3>
            <p>Turno Actual: {currentTurnos.melendez}</p>
            <p>Tiempo Estimado: {estimatedTime.melendez} minutos</p>
            <button
              className="request-button"
              onClick={() => handleRequestTurno('Melendez UCI')}
            >
              Solicitar Turno
            </button>
          </div>
          <div className="turno-info">
            <h3>Polvorines UCI</h3>
            <p>Turno Actual: {currentTurnos.polvorines}</p>
            <p>Tiempo Estimado: {estimatedTime.polvorines} minutos</p>
            <button
              className="request-button"
              onClick={() => handleRequestTurno('Polvorines UCI')}
            >
              Solicitar Turno
            </button>
          </div>
        </div>

        {/* Selector de Tipo de Servicio */}
        <div className="service-selector">
          <label className="pedir-turno-label">Tipo de Servicio:</label>
          <select
            value={tipoCita}
            onChange={(e) => setTipoCita(e.target.value)}
            className="pedir-turno-input"
          >
            <option value="">Selecciona un servicio</option>
            <option value="medica">Médica</option>
            <option value="administrativa">Administrativa</option>
          </select>
        </div>

        {/* Publicidad */}
        <div className="advertisement">
          <h3>Publicidad</h3>
          <img
            src="https://media.giphy.com/media/3o7TKz9bDaN3jksX6o/giphy.gif"
            alt="Publicidad"
            className="ad-gif"
          />
          {/* Placeholder para Google Ads */}
          {/* <div className="google-ads-placeholder">Google Ads Aquí</div> */}
        </div>
      </div>
    </div>
  );
};

export default PedirTurno;
